
import streamlit as st

EGFR_INHIBITOR_SMILES = "C1=CC(=C(C=C1NC(=O)C=C)N=C2C=C(C(=C(C2=O)OC)OC)O)OC"
EGFR_SEQUENCE = ""

def initialize_session_state():
    if 'initialized' in st.session_state:
        return
    st.session_state.initialized = True
    st.session_state.drug_smiles = ''
    st.session_state.protein_sequence = ''
    st.session_state.prediction_results = None
    st.session_state.model = None
    st.session_state.config = None

def load_demo_data():
    st.session_state.drug_smiles = EGFR_INHIBITOR_SMILES
    st.session_state.protein_sequence = EGFR_SEQUENCE
    st.session_state.prediction_results = None
